INSERT INTO posts (user_id, title, content, image_url, created_at) VALUES
                                                                       (1, '周末的Brunch时光 🥐', '发现了一家超棒的早午餐店！他们家的可颂外酥内软，咖啡也很香醇。环境特别适合拍照，推荐给大家！\n\n📍位置：市中心阳光路123号\n💰人均：80元\n⭐评分：4.5/5', 'https://images.unsplash.com/photo-1551218808-94e220e084d2?w=500', '2024-10-01 09:30:00'),
                                                                       (1, '秋天的第一杯奶茶 🌸', '终于喝到了网红奶茶店的秋季限定！桂花乌龙奶茶真的绝了，茶香和奶香完美融合，不会太甜腻。\n\n推荐指数：★★★★★', 'https://images.unsplash.com/photo-1567095761054-7a02e69e5c43?w=500', '2024-10-02 15:20:00'),
                                                                       (2, '西藏之旅 · 纳木错的星空 🌌', '在海拔4718米的纳木错，看到了这辈子最震撼的星空。银河清晰可见，仿佛伸手就能触摸到星辰。\n\n旅行tips：\n1. 一定要做好防高反准备\n2. 晚上很冷，带厚衣服\n3. 相机要带三脚架', 'https://images.unsplash.com/photo-1502136969935-8d8eef54d77b?w=500', '2024-10-03 20:15:00'),
                                                                       (2, '云南大理 · 风花雪月', '苍山洱海，风花雪月。在大理的每一天都像在画中游。\n\n推荐打卡地：\n✅ 洱海骑行\n✅ 大理古城\n✅ 崇圣寺三塔\n✅ 双廊古镇', 'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=500', '2024-10-04 11:45:00'),
                                                                       (3, '探店 | 隐藏在小巷的日式拉面 🍜', '这家拉面店真的惊艳到我了！汤底熬制12小时，面条筋道，叉烧入口即化。\n\n🍜 招牌豚骨拉面：45元\n🐔 唐扬鸡块：28元\n🥟 煎饺：22元\n\n环境：★★★★☆\n味道：★★★★★\n服务：★★★★☆', 'https://images.unsplash.com/photo-1569718212165-3a8278d5f624?w=500', '2024-10-05 18:30:00'),
                                                                       (3, '自制甜品 | 芒果慕斯蛋糕 🍰', '今天尝试做了芒果慕斯蛋糕，没想到第一次就成功了！口感细腻，芒果味浓郁。\n\n食材清单：\n• 芒果 2个\n• 淡奶油 200g\n• 吉利丁片 10g\n• 消化饼干 80g\n• 黄油 40g', 'https://images.unsplash.com/photo-1578985545062-69928b1d9587?w=500', '2024-10-06 14:00:00'),
                                                                       (4, 'OOTD | 秋季温柔系穿搭 🍂', '最近入秋了，分享一套温柔系的穿搭～\n\n👗 米色针织连衣裙：ZARA\n🧥 卡其色风衣：COS\n👢 棕色短靴：Charles & Keith\n👜 编织手提包：小众设计师品牌\n\n整体色调很温暖，适合秋天的各种场合～', 'https://images.unsplash.com/photo-1445205170230-053b83016050?w=500', '2024-10-07 10:15:00'),
                                                                       (4, '开箱 | 新入的化妆品合集 💖', '双十一第一波入手的美妆好物开箱！\n\n❤️ YSL皮气垫 - 妆效绝美\n💛 CT眼影盘 - 日常百搭\n💚 兰蔻菁纯口红 - 274号色超温柔\n💙 植村秀眉笔 - 新手友好\n\n有没有姐妹用过同款？', 'https://images.unsplash.com/photo-1596462502278-27bfdc403348?w=500', '2024-10-08 16:40:00'),
                                                                       (5, 'iPhone 15 Pro 深度体验 📱', '用了一周iPhone 15 Pro，来说说真实感受：\n\n👍 优点：\n• 钛金属边框确实轻了很多\n• 拍照效果提升明显\n• Action按钮很实用\n\n👎 缺点：\n• 价格偏高\n• 续航一般\n• 发热问题还是有', 'https://images.unsplash.com/photo-1592750475338-74b7b21085ab?w=500', '2024-10-09 13:25:00'),
                                                                       (5, '程序员的好物推荐 💻', '分享几个提升编程效率的工具：\n\n1. VS Code + GitHub Copilot - 代码补全神器\n2. iTerm2 + Oh My Zsh - 终端美化\n3. Alfred - 快速启动工具\n4. Notion - 知识管理\n5. TickTick - 任务管理', 'https://images.unsplash.com/photo-1542831371-29b0f74f9713?w=500', '2024-10-10 15:50:00'),
                                                                       (1, '居家好物 | 提升幸福感的小物件 🏠', '分享几个让生活更美好的小物件：\n\n🕯️ 香薰蜡烛 - 营造温馨氛围\n📚 迷你书架 - 收纳书籍和装饰品\n🌿 绿植 - 给家里增添生机\n☕ 手冲咖啡套装 - 享受慢生活', 'https://images.unsplash.com/photo-1586023492125-27b2c045efd7?w=500', '2024-10-11 12:00:00'),
                                                                       (2, '成都美食地图 🍢', '在成都待了三天，胖了五斤！整理了一份美食地图：\n\n🔥 火锅：小龙坎、蜀大侠\n🍢 串串：马路边边、钢管厂五区\n🍜 小吃：担担面、龙抄手、钟水饺\n🍮 甜品：冰粉、糍粑', 'https://images.unsplash.com/photo-1563245372-f21724e3856d?w=500', '2024-10-12 19:30:00'),
                                                                       (3, '烘焙日记 | 抹茶千层蛋糕 🍵', '今天挑战抹茶千层蛋糕，摊了20张饼皮，手臂已废但是值得！抹茶粉用的是日本宇治抹茶，味道很正。\n\n配方分享：\n• 低筋面粉 200g\n• 鸡蛋 4个\n• 牛奶 500ml\n• 抹茶粉 15g\n• 淡奶油 400g', 'https://images.unsplash.com/photo-1578985545062-69928b1d9587?w=500', '2024-10-13 16:20:00'),
                                                                       (4, 'Vlog | 上海时装周的一天 👗', '受邀参加上海时装周，看到了好多优秀的设计师作品！国潮真的越来越棒了～\n\n今日穿搭：\n👗 黑色西装连衣裙\n👠 银色高跟鞋\n💎 珍珠项链\n👜 黑色手拿包', 'https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=500', '2024-10-14 14:35:00'),
                                                                       (5, 'MacBook Pro M2 使用体验 💻', '新入的MacBook Pro M2用了两个月，来说说优缺点：\n\n✅ 性能强劲，编程无压力\n✅ 续航优秀，一天不用充电\n✅ 屏幕显示效果细腻\n❌ 价格较贵\n❌ 接口较少需要转接', 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?w=500', '2024-10-15 11:10:00');
